const express=require("express"),router=express.Router(),Product=require("./product");router.post("/",(async(req,res)=>{try{const product=await Product.create(req.body);res.status(201).json(product)}catch(err){res.status(400).json({message:err.message})}})),router.get("/",(async(req,res)=>{try{const products=await Product.find();res.json(products)}catch(err){res.status(500).json({message:err.message})}})),module.exports=router;